mapper.select:
SELECT d.district_id districtId, d.district_name district, d.center_lng centerLongitude, d.center_lat centerLatitude, count(a.PROD_INST_ID) hdUserNum FROM ocn_address.of_district d LEFT JOIN hsrjop.intelligence_hd_addr_info a ON d.district_id = of_district_id WHERE 1=1
AND d.district_id = #{districtId}
AND d.is_available = 1 GROUP BY a.of_district_id

SELECT re.*, d.center_lng cenLon, d.center_lat cenLat, sum(a.user_count) hdUserNum FROM hsrjop.of_residence a LEFT JOIN ocn_address.of_residence res on a.id = res.id, ( SELECT t.town_id townId, t.town_name townName, t.district_id districtId, t.latitude latitude, t.longitude longitude FROM ocn_address.of_town t WHERE t.latitude != ''
AND t.town_id = #{townId}
AND hsrjop.calcDistance(#{lng},#{lat},t.longitude,t.latitude) <8000
) re LEFT JOIN ocn_address.of_district d on d.district_id = re.districtId WHERE res.of_town_id = re.townId AND re.districtId = d.district_id
and d.district_id = #{districtId}
GROUP BY res.of_town_id HAVING hdUserNum != 0

SELECT re.*, d.longitude cenLon, d.latitude cenLat, sum(a.user_count) hdUserNum FROM hsrjop.of_residence a LEFT JOIN ocn_address.of_residence res on a.id = res.id, ( SELECT t.neighborhood_id neighborhoodId, t.neighborhood_name neighborhoodName, t.town_id townId, t.lng lng, t.lat lat FROM ocn_address.of_neighborhood t WHERE r.lng != ''
AND t.neighborhood_id = #{neighborhoodId}
AND hsrjop.calcDistance(#{lng},#{lat},t.lng,t.lat) <1500
) re, ocn_address.of_town d WHERE res.of_neighborhood_id = re.neighborhoodId AND res.of_town_id = d.town_id
AND re.townId = #{townId}
GROUP BY res.of_neighborhood_id HAVING hdUserNum != 0

SELECT re.*, a.user_count hdUserNum, a.house_count2 houseCount, FORMAT(a.user_count/a.house_count2*100,2) rate , n.lng cenLon, n.lat cenLat FROM hsrjop.of_residence a, ( SELECT r.id residenceId, r.residence_name residenceName, r.of_neighborhood_id neighborhoodId, r.lon lon, r.lat lat FROM ocn_address.of_residence r WHERE r.lon != ''
and r.id = #{residenceId}
and hsrjop.calcDistance ( #{lng}, #{lat}, r.lon, r.lat ) <800
) re ,ocn_address.of_neighborhood n WHERE re.residenceId = a.id and re.neighborhoodId = n.neighborhood_id
and re.neighborhoodId = #{neighborhoodId}
GROUP BY a.id HAVING hdUserNum != 0
