SELECT count(log_id) from (SELECT log.log_id, log.channel, log.mac, log.timestamp TIMESTAMP, log.session, m.district_id districtId, m.block_id blockId, m.residence_id residenceId FROM api_logs_shfctv log LEFT JOIN mac_info m ON log.mac = m.mac WHERE log.`timestamp` <= #{endTime} AND log.`timestamp` >= #{startTime}) temp WHERE temp.channel = #{log.channel}
AND temp.districtId = #{log.districtId}
AND temp.blockId = #{log.blockId}
AND temp.residenceId = #{log.residenceId}

SELECT count(DISTINCT temp.mac) from (SELECT log.log_id, log.channel, log.mac, log.timestamp TIMESTAMP, log.session, m.district_id districtId, m.block_id blockId, m.residence_id residenceId FROM api_logs_shfctv log LEFT JOIN mac_info m ON log.mac = m.mac WHERE log.`timestamp` <= #{endTime} AND log.`timestamp` >= #{startTime}) temp WHERE temp.channel = #{log.channel}
AND temp.districtId = #{log.districtId}
AND temp.blockId = #{log.blockId}
AND temp.residenceId = #{log.residenceId}

SELECT temp.mac FROM (SELECT log.log_id, log.channel, log.mac, log.timestamp TIMESTAMP, log.session, m.district_id districtId, m.block_id blockId, m.residence_id residenceId FROM api_logs_shfctv log LEFT JOIN mac_info m ON log.mac = m.mac WHERE log.`timestamp` <= #{endTime AND log.`timestamp` >= #{startTime}) temp WHERE temp.channel = #{log.channel}
AND temp.districtId = #{log.districtId}
AND temp.blockId = #{log.blockId}
AND temp.residenceId = #{log.residenceId}
AND temp.mac NOT IN (SELECT mac FROM new_visitor) GROUP BY temp.mac
