SELECT count(log_id) from (SELECT log.log_id, log.channel, log.mac, log.timestamp TIMESTAMP, log.session, m.district_id districtId, m.block_id blockId, m.residence_id residenceId FROM api_logs_shfctv_01 log LEFT JOIN mac_info m ON log.mac = m.mac WHERE log.`timestamp` <= FROM_UNIXTIME(#{endTime}, '%Y-%m-%d %H:%i:%s') AND log.`timestamp` >= FROM_UNIXTIME(#{startTime}, '%Y-%m-%d %H:%i:%s') ) temp WHERE temp.channel = #{log.channel}
AND temp.districtId = #{log.districtId}
AND temp.blockId = #{log.blockId}
AND temp.residenceId = #{log.residenceId}

SELECT count(DISTINCT temp.mac) from (SELECT log.log_id, log.channel, log.mac, log.timestamp TIMESTAMP, log.session, m.district_id districtId, m.block_id blockId, m.residence_id residenceId FROM api_logs_shfctv_01 log LEFT JOIN mac_info m ON log.mac = m.mac WHERE log.`timestamp` <= FROM_UNIXTIME(#{endTime}, '%Y-%m-%d %H:%i:%s') AND log.`timestamp` >= FROM_UNIXTIME(#{startTime}, '%Y-%m-%d %H:%i:%s') ) temp WHERE temp.channel = #{log.channel}
AND temp.districtId = #{log.districtId}
AND temp.blockId = #{log.blockId}
AND temp.residenceId = #{log.residenceId}

SELECT temp.mac FROM (SELECT log.log_id, log.channel, log.mac, log.timestamp TIMESTAMP, log.session, m.district_id districtId, m.block_id blockId, m.residence_id residenceId FROM api_logs_shfctv_01 log LEFT JOIN mac_info m ON log.mac = m.mac WHERE log.`timestamp` <= FROM_UNIXTIME(#{endTime}, '%Y-%m-%d %H:%i:%s') AND log.`timestamp` >= FROM_UNIXTIME(#{startTime}, '%Y-%m-%d %H:%i:%s') ) temp WHERE temp.channel = #{log.channel}
AND temp.districtId = #{log.districtId}
AND temp.blockId = #{log.blockId}
AND temp.residenceId = #{log.residenceId}
AND temp.mac NOT IN (SELECT mac FROM new_visitor) GROUP BY temp.mac

mapper.insert:
INSERT INTO `statistics`.his_trend_hour_${type} ( `channel_no`, `begin_time`, `end_time`, `uv`, `pv`, `nv`, `avg_top`,
`district_id`
block_id
residence_id
