mapper.insert:
INSERT INTO `statistics`.his_trend_hour_${type} ( `channel_no`, `begin_time`, `end_time`, `uv`, `pv`, `nv`, `avg_top`,
`district_id`
block_id
residence_id
) VALUES ( #{his.channelNo}, from_unixtime(#{his.timeBegin}), from_unixtime(#{his.timeEnd}), #{his.uv}, #{his.pv}, #{his.nv}, #{his.totalTop}/#{his.uv},
#{his.districtId}
#{his.blockId}
#{his.residenceId}
)
